import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import { createContext } from "react";

export const initialState = { theme: "", data: [] }

export const ContextGlobal = createContext(undefined);


export const ContextProvider = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo
  const [users, setUsers] = useState([])

  const [searchDentisId, setSearchDentisId] = useState({})

  const getDentistForId = async () => {
    try {
      const { data } = await axios.get(`https://jsonplaceholder.typicode.com/users/${searchDentisId}`)
      setSearchDentisId({ searchDentisId, ...data });
    } catch (error) {
      console.error(error);
    }
  }

  const getUsers = async () => {
    try {
      const { data } = await axios.get("https://jsonplaceholder.typicode.com/users")
      setUsers([...users, ...data]);
    } catch (error) {
      console.error(error);
    }
  }

  useEffect(() => {
    getUsers()
  }, [])

  useEffect(() => {
    getDentistForId()
  }, [setSearchDentisId])

  const data = {
    users,
    searchDentisId,
    setSearchDentisId
  }

  return (
    <ContextGlobal.Provider value={data}>
      {children}
    </ContextGlobal.Provider>
  );
};
